import { useState, useEffect } from "react";

async function getDataJson(subject, startIndex, maxResults) {
  startIndex = startIndex == null ? 0 : startIndex;
  maxResults = maxResults == null ? 10 : maxResults;
  const url = `https://www.googleapis.com/books/v1/volumes?q=subject:${subject}&startIndex=${startIndex}&maxResults=${maxResults}`;
  const response = await fetch(url);

  return response.json();
}

function Layout() {
  const [books, setBooks] = useState([]);

  useEffect(() => {
    getDataJson("Fiction", 0, 10).then((apiData) => {
      var tempBooks = [];
      apiData.items.map((val) => tempBooks.push(val));
      setBooks(tempBooks);
    });
  }, []);

  if (books.length > 0) {
    const titles = () => {
      var tempTitles = [];
      books.forEach((book) => {
        tempTitles.push(
          <li key={book["id"]}>{book["volumeInfo"]["title"]}</li>
        );
      });

      return tempTitles;
    };
    const stuff = () => {
      var tempStuff = {
        Title: [],
        Description: [],
        Id: [],
      };
      books.forEach((book) => {
        tempStuff.Title.push(<p key={book["volumeInfo"]}>{book["volumeInfo"]["title"]}</p>);
        tempStuff.Description.push(
          <p className="desc">{book["volumeInfo"]["description"]}</p>
        );
        tempStuff.Id.push(<p>{book["id"]}</p>);
      });

      return tempStuff;
    };

    const jhinga = () => {
      let _titles = stuff().Title;
      let _descs = stuff().Description;
      let _ids = stuff().Id;

      var idk = [];

      for (let i = 0; i < _ids.length; i++) {
        idk.push(
          <>
            <div className="col-2 card m-0 p-0">
              <div className="card-header">{_titles[i]}</div>
              <div className="card-body">{_descs[i]}</div>
              <div className="card-footer">{_ids[i]}</div>
            </div>
          </>
        );
      }

      return idk;
    };

    return (
      <>
        <div className="row">{jhinga()}</div>
      </>
    );
  } else {
    return <>{<p>Loading Books</p>}</>;
  }
}

export default Layout;
