import { useState, useEffect } from "react";

async function getDataJson(subject, startIndex, maxResults) {
  startIndex = startIndex == null ? 0 : startIndex;
  maxResults = maxResults == null ? 10 : maxResults;
  const url = `https://www.googleapis.com/books/v1/volumes?q=subject:${subject}&startIndex=${startIndex}&maxResults=${maxResults}`;
  const response = await fetch(url);

  return response.json();
}

function Layout() {
  const [books, setBooks] = useState([]);

  useEffect(() => {
    getDataJson("Fiction", 0, 10).then((apiData) => {
      var tempBooks = [];
      apiData.items.map((val) => tempBooks.push(val));
      setBooks(tempBooks);
    });
  }, []);

  if (books.length > 0) {
    const titles = () => {
      var tempTitles = [];
      books.forEach((book) => {
        tempTitles.push(
          <li key={book["volumeInfo"]["id"]}>{book["volumeInfo"]["title"]}</li>
        );
      });

      return tempTitles;
    };
    return (
      <>
        <ul>{title()}</ul>
      </>
    );
  } else {
    return (
      <>
        <p>Loading Books</p>
      </>
    );
  }
}

export default Layout;
